import UIKit

enum Login
{
  // MARK: Use cases
  
  enum Fetch
  {
    struct Request
    {
        let data: [String: String]
        
    }
    struct Response: Codable
    {
        var userAccount: UserData?
        var error: ErrorData?
    }
    struct ViewModel
    {
        var userAccount: UserData?
        var errorMessage: String?
    }
    
    struct UserData: Codable{
        let userId: Int?
        let name: String?
        let bankAccount: String?
        let agency: String?
        let balance: Float?
    }
    
    struct ErrorData: Codable{
        var code: Int?
        var message: String?
    }
    
    struct UserLoginData{
        var user: String?
        var password: String?
    }
  }
}
