import UIKit

protocol LoginBusinessLogic
{
    func fetchUserAccount(request: Login.FetchUser.Request)
    func checkLastUser()
}

protocol LoginDataStore
{
    //var name: String { get set }
    var userAccount: UserAccount? { get }
}

class LoginInteractor: LoginBusinessLogic, LoginDataStore
{
    var presenter: LoginPresentationLogic?
    var worker: LoginWorker = LoginWorker(bankStore: BankAPI())
    var userAccount: UserAccount?
    
    let keychainCredentialsKey = "bank_app_user_credentials"
    
    // MARK: Fetch user account given user credentials
    
    func checkLastUser() {
        let base64StoredCredentials = KeyChainService.shared[keychainCredentialsKey]
        if let base64StoredCredentials = base64StoredCredentials,
            let data = base64StoredCredentials.data(using: .utf8),
            let b64Data = Data(base64Encoded: data),
            let dataStr = String(data: b64Data, encoding: .utf8){
            let credentials = try? JSONDecoder().decode(UserCredentials.self, from: dataStr.data(using: .utf8)!)
            if let credentials = credentials{
                presenter?.presentCurrentSavedUser(userCredentials: credentials)
            }else{
                presenter?.presentCurrentSavedUser(userCredentials: UserCredentials(emailOrCPF: "", password: ""))
            }
        }else{
            presenter?.presentCurrentSavedUser(userCredentials: UserCredentials(emailOrCPF: "", password: ""))
        }
    }
    
    
    func fetchUserAccount(request: Login.FetchUser.Request)
    {
//        if request.credentials.emailOrCPF.isEmpty{
//            presenter?.presentValidationErrors(message: "Please inform the username!")
//            return
//        }
        if request.credentials.password.isEmpty{
            presenter?.presentErrorMessage(message: "Please inform the password!")
            return
        }
        if !isValidPassword(password: request.credentials.password){
            presenter?.presentErrorMessage(message: "The password should have at least an uppercase letter, a lowercase letter, an alpha numeric character and a special character")
            return
        }
        
        worker.fetchUserAccount(userCredentials: request.credentials, completionHandler: { (response, error) in
            if let error = error{
                self.presenter?.presentErrorMessage(message: error.localizedDescription)
                return
            }
            if let errorMessage = response?.error?.message,
                let errorCode = response?.error?.code{
                self.presenter?.presentErrorMessage(message: "Code: \(errorCode)\n\(errorMessage)")
                return
            }
            if let response = response{
                self.userAccount = response.userAccount
                self.presenter?.presentUserData(response: response)
                if let dataToStore = try? JSONEncoder().encode(request.credentials){
                    KeyChainService.shared[self.keychainCredentialsKey] = dataToStore.base64EncodedString()
                }
            }
        })
    }
    
    private func isValidPassword(password: String) -> Bool{
        let characterset = CharacterSet(charactersIn: "abcdefghijklmnopqrstuvwxyzABCDEFGHIJKLMNOPQRSTUVWXYZ0123456789")
        let containsUppercase = password.contains(where: {$0.isUppercase})
        let containsLowercase = password.contains(where: {$0.isLowercase})
        let containsNumber = password.contains(where: {$0.isNumber})
        let containsSpecialChar = password.rangeOfCharacter(from: characterset.inverted) != nil
        return containsUppercase && containsNumber && containsSpecialChar && containsLowercase
    }
}
