import UIKit

enum LoginError: Error{
    case CannotLogin(String)
}

class LoginWorker
{
    var bankStore: BankUserStoreProtocol
    
    init(bankStore: BankUserStoreProtocol) {
        self.bankStore = bankStore
    }
    
    func fetchUserAccount(userCredentials: UserCredentials,
                          completionHandler: @escaping (Login.FetchUser.Response?, LoginError?) -> Void){
        bankStore.fetchUserAccount(userCredentials: userCredentials) { (userAccountReponse, error) in
            completionHandler(userAccountReponse, error)
        }
    }
}

protocol BankUserStoreProtocol{
    func fetchUserAccount(userCredentials: UserCredentials, completionHandler: @escaping (Login.FetchUser.Response?, LoginError?) -> Void)
}
