import UIKit

class UserBalanceWorker
{
    var bankStore: BankStatementsStoreProtocol
    
    init(bankStore: BankStatementsStoreProtocol) {
        self.bankStore = bankStore
    }
    
    func fetchStatements(userAccount: UserAccount,
                         completionHandler: @escaping (UserBalance.FetchStatements.Response?, UserBalanceError?) -> Void){
        bankStore.fetchStatements(userAccount: userAccount,
                                  completionHandler: completionHandler)
    }
}

protocol BankStatementsStoreProtocol{
    func fetchStatements(userAccount: UserAccount,
    completionHandler: @escaping (UserBalance.FetchStatements.Response?, UserBalanceError?) -> Void)
}
